import React,{useState} from "react";
import '../sliders/slider.css';


export default function Slider(props) {
const {slides} = props;



const [active,setActive] = useState(0)




function prevSlide() {
    
    setActive(active < 1 ?  slides.len : active - 1)
}
    

function nextSlide() {
    
    setActive(active ===  slides.len ? 0 : active + 1 )
}
    return (
        <div className="slider">
            <button className="prevSlide" onClick={prevSlide} ></button>
           
                <div className = 'sliderTrack'>
                {
                    slides.map(
                        slide => {
                            return <a className="slide">

                                <img className="slideImg" key = {slide} src={slide.image} alt={''}></img>; 
                                </a>

                        }
                    )
                }
                </div>
           
            <button className="nextSlide" onClick={nextSlide} ></button>
        </div>
    )
}
