import React,{useState} from "react";
import '../sliders/slider.css';


export default function Slider(props) {
const {slides} = props;

const [next,setNext] = useState(slides)

function nextSlide() {

    setNext()
}
function backSlide() {

    setNext()
}
    
    return (
        <div className="slider">
            <div className="slide">
                {
                    slides.map(
                        slide => {
                            return <img className="slideImg" key = {slide} src={slide.image} alt={''}></img>; 
                            

                        }
                    )
                }
            </div>
        </div>
    )
}
