import React,{useState} from "react";
import '../sliders/slider.css';


export default function Slider(props) {
const {slides} = props;

const [next,setNext] = useState(slides)

function nextSlide() {

    setNext()
}
function backSlide() {

    setNext()
}
    
    return (
        <div className="slider">
            <button className="nextSlide" onClick={nextSlide} ></button>
           
                
                {
                    slides.map(
                        slide => {
                            return <div className="slide">
                                <img className="slideImg" key = {slide} src={slide.image} alt={''}></img>; 
                                </div>

                        }
                    )
                }
                
           
            <button className="backSlide" onClick={backSlide} ></button>
        </div>
    )
}
