import React,{useState,useEffect} from "react";
import '../sliders/slider.css';


export default function Slider(props) {
const {slides} = props;



const [next,setNext] = useState(0)



useEffect(() => {
    // Запускаем интервал
    const interval = setInterval(() => {
        // Меняем состояние
        setActiveIndex((current) => {
            // Вычисляем индекс следующего слайда, который должен вывестись
            const res = current === img.length - 1 ? 0 : current + 1
            // Возвращаем индекс
            return res
        })
    }, 3000)
    // Выключаем интервал
    return () => clearInterval()
}, [])
function nextSlide() {
    const nextImg = next === slides.length - 1 ? 0 : next + 1
    setNext(nextImg )
}
function prevSlide() {
    const prevImg = next ? next- 1 : slides.length - 1
    setNext(prevImg)
}
    
    return (
        <div className="slider">
            <button className="nextSlide" onClick={nextSlide} ></button>
           
                
                {
                    slides.map(
                        slide => {
                            return <a className="slide">

                                <img className="slideImg" key = {slide} src={slide.image} alt={''}></img>; 
                                </a>

                        }
                    )
                }
                
           
            <button className="prevSlide" onClick={prevSlide} ></button>
        </div>
    )
}
