import React,{useState} from "react";
import '../sliders/slider.css';


export default function Slider(props) {
const {slides} = props;



const [next,setNext] = useState(0)




function nextSlide() {
    const nextImg = next === slides.length - 1 ? 0 : next + 1
    setNext(nextImg )
}
function prevSlide() {
    const prevImg = next ? next- 1 : slides.length - 1
    setNext(prevImg)
}
    
    return (
        <div className="slider">
            <button className="nextSlide" onClick={nextSlide} ></button>
           
                <div className = 'sliderTrack'>
                {
                    slides.map(
                        slide => {
                            return <a className="slide">

                                <img className="slideImg" key = {slide} src={slide.image} alt={''}></img>; 
                                </a>

                        }
                    )
                }
                </div>
           
            <button className="prevSlide" onClick={prevSlide} ></button>
        </div>
    )
}
